import firebase from "firebase";

function UserDisplayPair(props) {
    const partner = props.partner; // should be id of partner
    const db = firebase.firestore();

    const getPartnerInfo = async () => {
        console.log(partner)
        console.log(props.currPoll)
        let partnerData = (await db.collection("surveys").doc(props.currPoll).collection("responses")
            .doc("rw3aRTlAeR65ze6A0778").get()).exists;

        console.log(partnerData);
    }

    getPartnerInfo();
    return (
        <div>
            <div><strong>You've been paired with {props.partner}!</strong></div>
        </div>
    );
}

export default UserDisplayPair;
