import React from "react";
import TextBox from './components/TextBox'


const Login = (props) => {

    // referenced https://www.w3schools.com/howto/howto_js_toggle_password.asp
function toggleVisibility() {
  var x = document.getElementById("passwordInput");
  if (x.type === "password") {
    x.type = "text";
  } else {
    x.type = "password";
  }
}
    // decompose props
    const {email,
      setEmail,
      password,
      setPassword,
      handleLogin,
      handleSignup,
      hasAccount,
      setHasAccount,
      emailError,
      passwordError
    } = props;


    return (
        <section className = "login">
            <div className = "loginContainer">
                 {hasAccount ? (<h1>Log In</h1>) : (<h1>Sign Up</h1>)}
                <br></br>
                <TextBox type={"text"} label={"Username/Email"} focus={true} value={email} change={setEmail}/>
                <p className="errorMsg">{emailError}</p>
                <TextBox type={"password"} id="passwordInput" label={"Password"} focus={false} value={password} change={setPassword}/>
                 <input 
                    type="password"
                    required
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />



                 <p className="errorMsg">{passwordError}</p>
                 <br></br>
                 <input id="showPassword" type="checkbox" label="Show Password" onClick={()=>toggleVisibility()}/>
                 <div className = "btnContainer">
                     {hasAccount ? (
                         //hasAccount is true
                         <>
                         <button onClick={handleLogin}>Sign in</button>
                         <p>
                             Don't have an account ? 
                             <span onClick={() => setHasAccount(!hasAccount)}>Sign up</span>
                             </p>
                         </>
                     ): (
                        <>
                        <button onClick={handleSignup}>Sign up</button>
                         <p>
                             Have an account ? 
                             <span onClick={() => setHasAccount(!hasAccount)}>Sign in</span>
                        </p>
                         </>
                     )}
                 </div>
            </div>
        </section>

    )
}

export default Login;
