import React, { useState, useEffect } from "react";
import TextBox from './components/TextBox';
import PopUp from './components/PopUp';

const SignUp = (props) => {

    // referenced https://www.w3schools.com/howto/howto_js_toggle_password.asp

    const [agreed, setAgreed] = useState(false)
    const [agreedError, setAgreedError] = useState('')


    // decompose props
    const {email,
      setEmail,
      password,
      setPassword,
      handleLogin,
      handleSignup,
      hasAccount,
      setHasAccount,
      emailError,
      passwordError,

       popUpSeen, 
      setPopUpSeen,
      toggleVisibility,
      visibility,
      userName,
      setUserName,


    } = props;

useEffect(() => {
    if(popUpSeen) {
        <PopUp toggle={props.setPopUpSeen} />
    }
    return () => {

    }
}, [popUpSeen])

// useEffect(() => {
//     if(agreed === true) {
//         setAgreedError("")
//     } else {
//         setAgreedError("Agree to the privacy policy to create an account")
//     }
//     return () => {

//     }
// }, [agreedError])

    return (
        <section className = "login">
            <div className = "loginContainer">
                <h1>Sign Up</h1>
                <br></br>
                <TextBox type={"text"} label={"Name"} focus={true} value={userName} change={setUserName}/>
                <TextBox type={"text"} label={"Email"} focus={false} value={email} change={setEmail}/>
                <p className="errorMsg">{emailError}</p>
                <TextBox type={visibility} label={"Password"} focus={false} value={password} change={setPassword}/>
                 <p className="errorMsg">{passwordError}</p>
                 <br/>
                 <div className="small">
                    Show Password: <input  type="checkbox" id="showPassword" onClick={()=>toggleVisibility()}/> 
                 </div>
                 <div className="agreePrivacyContainer">  
                        <span onClick={()=>setPopUpSeen(true)}> I accept the <u>Privacy Policy:  </u> </span>
                        <input  type="checkbox" id="agreePrivacy" onClick={() => setAgreed(true)}/>
                        {popUpSeen===true ? <PopUp toggle={props.setPopUpSeen} /> : null}
                 </div>



                    <div className = "btnContainer">
                        <button onClick={() => agreed ? handleSignup() : setAgreedError("Agree to the privacy policy to create an account")}>Sign up</button>
                         <p>
                             Have an account ? 
                             <span onClick={() => setHasAccount(!hasAccount)}>Sign in</span>
                        </p>


                 </div>
                  <p className="errorMsg">{agreedError}</p>
            </div>
        </section>

    )
}
export default SignUp;

