import React, { useState, useEffect } from "react";
import TextBox from './components/TextBox'


const Login = (props) => {
    const [visibility, setVisiblity] = useState("password")
    // referenced https://www.w3schools.com/howto/howto_js_toggle_password.asp

    function toggleVisibility() {
    if (visibility === "password") {
        setVisiblity("text");
    } else {
        setVisiblity("password");
    }
    }

    // decompose props
    const {email,
      setEmail,
      password,
      setPassword,
      handleLogin,
      handleSignup,
      hasAccount,
      setHasAccount,
      emailError,
      passwordError
    } = props;

    //<TextBox type={"password"} id="passwordInput" label={"Password"} focus={false} value={password} change={setPassword}/>
//<label className="small" htmlFor="showPassword">Show Password</label>

    return (
        <section className = "login">
            <div className = "loginContainer">
                 {hasAccount ? (<h1>Log In</h1>) : (<h1>Sign Up</h1>)}
                <br></br>
                <TextBox type={"text"} label={"Username/Email"} focus={true} value={email} change={setEmail}/>
                <p className="errorMsg">{emailError}</p>
                <TextBox type={visibility} label={"Password"} focus={false} value={password} change={setPassword}/>
                 <p className="errorMsg">{passwordError}</p>
                 <br/>
                 <div className="small">
                    Show Password: <input  type="checkbox" id="showPassword" onClick={()=>toggleVisibility()}/> 
                 </div>
                 <div className = "btnContainer">
                     {hasAccount ? (
                         //hasAccount is true
                         <>
                         <button onClick={handleLogin}>Sign in</button>
                         <p>
                             Don't have an account ? 
                             <span onClick={() => setHasAccount(!hasAccount)}>Sign up</span>
                             </p>
                         </>
                     ): (
                        <>
                        <div> 
                            <div><Link to="/Components/PopUp">Click here to go back to home page</Link></div>
                        </div>
                        <button onClick={handleSignup}>Sign up</button>
                         <p>
                             Have an account ? 
                             <span onClick={() => setHasAccount(!hasAccount)}>Sign in</span>
                        </p>
                         </>
                     )}
                 </div>
            </div>
        </section>

    )
}

export default Login;
