import React, {useState, useEffect} from 'react'
import Survey from "../Alana_stuff/Survey";
import firebase from "firebase";
import Question from "../Alana_stuff/Question";
import DisplaySurvey from "../Alana_stuff/DisplaySurvey";

function SharedWithMe() {
    const [allSurveys, setSurveys] = useState([])
    const db = firebase.firestore();

    // load in all the surveys.
    const loadInfo = async () => {
        let temp = []
        const surveys =
            (await db.collection("surveys").get()).docs.map(d => d);
        surveys.forEach(survey => {
            temp.push(survey)
        })
        setSurveys(temp)
        console.log(temp)
    }

    // on page load, load in the survey
    useEffect(() => {
        loadInfo();
    }, [])

    return (
        <div>
            {allSurveys.map((s, sid) =>
                <DisplaySurvey title={s.title} question={s.description}/>
            )}
        </div>
    )
}

export default SharedWithMe
