import React from "react";


const login = (props) => {

    // decompose props
    const {email,
      setEmail,
      password,
      setPassword,
      handleLogin,
      handleSignup,
      hasAccount,
      setHasAccount,
      emailError,
      passwordError
    } = props;


    return (
        <section className = "login">
            <div className = "loginContainer">
                <label htmlFor="Username">Username</label>
                <input 
                    type="text"
                    autoFocus
                    required
                    value={email}
                    onChange={(e) => setPassword(e.target.value)}
                />
            </div>
        </section>
    )
}

export default login;
