import Option from "./Option";
import React, {useEffect, useState} from "react";
import firebase from "firebase";
import Question from "./Question";
import {Link} from "react-router-dom";

import ViewOnlyQuestion from "./ViewOnlyQuestion";
import ResponseComparison from "./ResponseComparison";

function ViewOnlySurvey(props) {
    const [title, setTitle] = useState("loading...");
    const [description, setDescription] = useState("almost there...");
    const [questions, setQuestions] = useState([]);

    const [error, setError] = useState("");
    const db = firebase.firestore();

    useEffect(() => {
        getSurveyInfoFromDatabase();
    }, [])

    const getSurveyInfoFromDatabase = async () => {
        let currentSurveyRef = db.collection("surveys").doc(props.currPoll);

        setTitle((await currentSurveyRef.get()).data().title);
        setQuestions((await currentSurveyRef.collection("questions").get()).docs.map(d => d.data()));
        // setUserAnswers(await )
    }

    return (<div>
            <div className="poll">
                <div className="surveyInfo">
                    <h1>{title}</h1>
                    <p>{description}</p>
                </div>
                {questions.map((q, qid) =>
                    <ViewOnlyQuestion options={q.options} question={q.question} id={qid}/>
                )}

                <div>
                    {/*<ResponseComparison userData={props.userData} partnerData={props.partnerData}/>*/}
                </div>
            </div>
        </div>
    )
}

export default ViewOnlySurvey;
