import firebase from "firebase";
import {useEffect, useState} from "react/cjs/react.production.min";

function UserDisplayPair(props) {

    useEffect(() => {
        getPartnerInfo();
    }, [])

    const partner = props.partner; // should be id of partner
    const db = firebase.firestore();

    const getPartnerInfo = async () => {
        return (await db.collection("surveys").doc(props.currPoll).collection("responses")
            .doc(partner).get()).data();
    }

    return (
        <div>
            <div><strong>You've been paired with A!</strong></div>
        </div>
    );
}

export default UserDisplayPair;
