import firebase from "firebase";
import {useEffect, useState} from "react/cjs/react.production.min";

function UserDisplayPair(props) {
    const [partnerData, setPartnerData] = useState({});

    useEffect(() => {
        setPartnerInfo()
    }, [])

    const partner = props.partner; // should be id of partner
    const db = firebase.firestore();

    const setPartnerInfo = async () => {
        setPartnerData((await db.collection("surveys").doc(props.currPoll).collection("responses")
            .doc(partner).get()).data());
    }

    if (partnerData) {
        return (
            <div>
                <div><strong>You've been paired with {partnerData.userID}!</strong></div>
            </div>
        );
    }
}

export default UserDisplayPair;
