import Option from "./Option";
import React, {useEffect, useState} from "react";
import firebase from "firebase";
import Question from "./Question";
import {Link} from "react-router-dom";

function ViewOnlySurvey(props) {
    const [title, setTitle] = useState("loading...");
    const [surveyCreator, setCreator] = useState(""); // use this to check if button should show up
    const [description, setDescription] = useState("almost there...");
    const [questions, setQuestions] = useState([]);
    const [userAnswers, setUserAnswers] = useState([]);
    const [error, setError] = useState("");
    const db = firebase.firestore();

    useEffect(() => {
        getSurveyInfoFromDatabase();
    })

    const getSurveyInfoFromDatabase = async () => {
        let currentSurveyRef = db.collection("surveys").doc(props.currPoll);

        let ques = (await currentSurveyRef.collection("questions").get()).docs.map(d => d.data());
        console.log(ques);
    }

    return (<div>
            <div className="poll">
                <div className="surveyInfo">
                    <h1>{title}</h1>
                    <p>{description}</p>
                </div>
                {questions.map((q, qid) =>
                    <Question options={q.options} question={q.question} id={qid}/>
                )}
            </div>
        </div>
    )
}

export default ViewOnlySurvey;
