import React, {useState, useEffect} from 'react'
import Survey from "../Alana_stuff/Survey";
import firebase from "firebase";
import Question from "../Alana_stuff/Question";
import DisplaySurvey from "../Alana_stuff/DisplaySurvey";

function SharedWithMe() {
    const [allSurveys, setSurveys] = useState([])
    const db = firebase.firestore();

    // load in all the surveys.
    const loadInfo = () => {
        let temp = []
    // Print each document
        db.collection("surveys").onSnapshot((querySnapshot) => {
                querySnapshot.forEach((doc) => {
                    temp.push(doc)
                })
            });
        setSurveys(temp)
    }

    // on page load, load in the survey
    useEffect(() => {
        loadInfo();
    }, [])

    return (
        <div>
            {allSurveys.map((s) =>
                <DisplaySurvey data={s.data()} link={s.id}/>
            )}
        </div>
    )
}

export default SharedWithMe
