import firebase from "firebase";

function UserDisplayPair(props) {
    const partner = props.partner; // should be id of partner
    const db = firebase.firestore();

    const getPartnerInfo = async () => {
        let responsesRef = db.collection("surveys").doc(props.currPoll).collection("responses")
    }

    return (
        <div>
            <div><strong>You've been paired with {props.partner}!</strong></div>
        </div>
    );
}

export default UserDisplayPair;
